import React, { Component } from 'react'
import Timepicker from 'react-time-picker'
import moment from 'moment'
class Input extends Component{
    state={
        task:'',
        taskList:[],
        starttime:"10:00",
        endtime:"00:00",
        diff:0
      };
    //   onChange = event => 
    //   {
    //       this.setState({ starttime:event.target.value })
    //   }
      onChange = starttime => 
      {
          this.setState({ starttime })
      }
      onChangeEnd = event => 
      {
          this.setState({ endtime:event.target.value })
      }
      onChangeEnd = event => 
      {
          this.setState({ endtime:event.target.value })
      }
      inputChangeHandler = (input)=>{
          this.setState({
            task: input
          });
      }
      btnAddHandler = (inputTask,inputTime,endtime)=>{
        var start = moment.duration(inputTime, "HH:mm");
        var end = moment.duration(endtime, "HH:mm");
        var diff1 = end.subtract(start);
        let item = {
          value: inputTask,
          starttime: start,
          endtime:end,
          diff:diff1.hours
        }
        let newList = [
            ...this.state.taskList,
            item
          ]
          this.setState({
            taskList:newList,
            task: ''
          })
        }
    render()
    {
        return(
            <div>
                <input type="text" placeholder="Enter activity" onChange={(event)=>this.inputChangeHandler(event.target.value)}/><br></br>
                <label>Enter start time</label>
                {/* <Timepicker 
                onChange={this.onChange}
                value={this.state.starttime}> </Timepicker><br></br> */}
                <input type="time"
                onChange={this.onChange}
                value={this.state.starttime}/><br></br>
                <label>Enter end time</label>
                {/* <Timepicker
                onChange={this.onChangeEnd}
                value={this.state.starttime}/> */}
                <input type="time"
                onChange={this.onChangeEnd}
                value={this.state.endtime}/><br></br>
                <button id="btn1"
                type="submit"
                onClick={()=> this.btnAddHandler(this.state.task,this.state.starttime,this.state.endtime)} >Add</button>
                <div>
              {this.state.taskList.map((todo,index) =>
              <div>{todo.value}{todo.diff}</div>
              )}
              </div>
            </div>
        )
    }
}
export default Input
