import React, { Component } from 'react'
import Timepicker from 'react-time-picker/dist/entry.nostyle'

class Input extends Component{
    state={
        task:'',
        taskList:[],
        starttime:"10:00"
      };
      onChange = time => this.setState({ time })
      inputChangeHandler = (input)=>{
          this.setState({
            task: input
          });
      }
      btnAddHandler = (inputTask)=>{
        let item = {
          value: inputTask,
          done: false,
          edit: false
        }
        let newList = [
            ...this.state.taskList,
            item
          ]
          this.setState({
            taskList:newList,
            task: ''
          })
        }
    render()
    {
        return(
            <div>
                <input type="text" placeholder="Enter activity" onChange={(event)=>this.inputChangeHandler(event.target.value)}/><br></br>
                <label>Enter start time</label>
                <Timepicker > </Timepicker><br></br>
                <label>Enter end time</label>
                <Timepicker/>
                <button id="btn1"
                type="submit"
                onClick={()=> this.btnAddHandler(this.state.task)} >Add</button>
                <div>
              {this.state.taskList.map((todo,index) =>
              <div>{todo.value}</div>)}
              </div>
            </div>
        )
    }
}
export default Input
