import React, { Component } from 'react'
import {Redirect} from 'react-router-dom'
import Input from '../input'

class SignUp extends Component
{
    state={
        username:"",
        password:""
    }

    HandleSubmit()
    {
        var inputUsername = localStorage.getItem("username")
        if(inputUsername==null)
        {
        localStorage.setItem('username',this.state.username)
        localStorage.setItem('password',this.state.password)
        }
    //     else{
    //         var inputPassword = localStorage.getItem("password");
    //     if (this.state.username != "undefined" || this.state.username != "null" || this.state.password != "undefined" || this.state.password !="null")
    //     {
    //         <Redirect to="/"/>
    //     }
    // }
    }
    HandleChange(event)
    {
        this.setState({username:event.target.value})
    }
    HandlePassword(event)
    {
        this.setState({password:event.target.value})
    }
    render()
    {
        return(
            <div>
                <form onSubmit={this.HandleSubmit}>
                <input type="text" placeholder="username" onChange={this.HandleChange}/>
                <input type="password" placeholder="password" onChange={this.HandlePassword}/>
                <input type="submit" placeholder="signup"/>
                </form>
            </div>
        )
    }
}

export default SignUp;
