import React, { Component } from 'react'
import Timepicker from 'react-time-picker'

class Input extends Component{
    state={
        task:'',
        taskList:[],
        starttime:[],
        endtime:[],
        diff:0
      };
      onChange = starttime => 
      {
          let time=this.starttime
          let newTime=[
              ...this.state.starttime,
            time]
            this.setState({starttime:newTime})
      }
      inputChangeHandler = (input)=>{
          this.setState({
            task: input
          });
      }
      btnAddHandler = (inputTask)=>{
        let item = {
          value: inputTask,
          done: false,





















          edit: false
        }
        let newList = [
            ...this.state.taskList,
            item
          ]
          this.setState({
            taskList:newList,
            task: ''
          })
        }
    render()
    {
        return(
            <div>
                <input type="text" placeholder="Enter activity" onChange={(event)=>this.inputChangeHandler(event.target.value)}/><br></br>
                <label>Enter start time</label>
                <Timepicker 
                onChange={this.onChange}
                value={this.state.starttime}> </Timepicker><br></br>
                <label>Enter end time</label>
                <Timepicker/>
                <button id="btn1"
                type="submit"
                onClick={()=> this.btnAddHandler(this.state.task)} >Add</button>
                <div>
              {this.state.taskList.map((todo,index) =>
              <div>{todo.value}{this.state.starttime}</div>
              )}
              </div>
              <div>
              {this.state.starttime.map((todo,index) =>
              <div>{todo.value}</div>
              )}
              </div>
            </div>
        )
    }
}
export default Input
